import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.DisplayName;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.assertEquals;public class DistanceCalculatorTest {        DistanceCalculator calculator;        @BeforeEach    void init() {        calculator = new DistanceCalculator();    }        @Test    @DisplayName("должен верно рассчитывать коэффициент хрупкости")    void shouldGetFragilityCoefficientTrue() {        double fragility = 35;        assertEquals(300, calculator.calculateCost(fragility), "коэффициент должен быть равен 300");    }}